import React from 'react'
import {useState ,useEffect } from 'react'
import Axios from 'axios';

function Popular() {

    const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';
    [weekMostPopular, setWeekMostPopular] = useState({})

  function getPopMovies(){
    Axios.get(popMoviesUrl)
      .then(res => setWeekMostPopular(res.data.results))
  }

  const createMovEl = (data)=>{
    console.log(data)


    data.map(movie => {
      const baseUrlImg = 'http://image.tmdb.org/t/p/w500';
      const {title, poster_path, vote_average} = movie;
      const movieEl = document.createElement('div');
      movieEl.className = 'popMovie';
      movieEl.innerHTML = `
      <h3>${title}</h3>
      <img  src="${baseUrlImg + poster_path}" alt="${title}" />
      <span>${vote_average}</span>
      `
    });
  }

    getPopMovies() 

  return (
    <div id = 'most_popular'>

        <div className="popMovie">

        <h3>{title}</h3>
      <img  src={baseUrlImg + poster_path} alt={title} />
      <span>{vote_average}</span>

        </div>

    </div>
  )
}

export default Popular
