import React from 'react'
import {useEffect } from 'react'
import Axios from 'axios';

function Popular() {

    const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';
    const baseUrlImg = 'http://image.tmdb.org/t/p/w500';
    let movies = {};
  const getPopMovies = ()=>{
    Axios.get(popMoviesUrl)
      .then(res => structurePopMovies(res.data.results))
  }

  const structurePopMovies = (data)=>{ 
    data.forEach(movie => (
      <div>
      <h3>{movie.title}</h3>
      <img  src={baseUrlImg + movie.poster_path} alt={movie.title} />
      <span>{movie.vote_average}</span>
      </div>))
   };

   useEffect(getPopMovies())

   let movieCard = movies.


   return (
     <section>
      <h1>Most Popular This week</h1>
    <div id = 'most_popular'>
        <div className="popMovie">
          {movieCard}
        </div>

    </div>
    </section>
  )
}


export default Popular
