import React from 'react'

export default function SinglePage(props) {
    const imgBaseUrl = 'http://image.tmdb.org/t/p/w500';
    const movie = props.clickedMovieData;

    const setListWached = ()=>{
      const storedList = localStorage.getItem('listWatched');
      localStorage.setItem('listWatched', (storedList + JSON.stringify(movie)))
    };

  return (
    <div className='single_page'>
       <h2>{props.clickedMovieData.title}</h2> 
       <img src={imgBaseUrl+movie.poster_path} alt={movie.title}/>
       <span>Rate: {movie.vote_average}</span>
       <p>{ movie.overview }</p>
       <div>
           <h3>Add to:</h3>
       <button onClick = {()=>{setListWached()}} >Watched</button>
       <button onClick = {()=>{localStorage.setItem('listToWatch', JSON.stringify(movie))}}>To Watch</button>
       </div>
       <button className="close_btn" onClick={()=>{props.closeSinglePage('closed')}}>X</button>

    </div>
  )
}
