import './App.css';
import Home from './components/home/Home'
import Library from './components/library/Library'
import Search from './components/search/Search'
import SinglePage from './components/SinglePage'
import {useState} from 'react'
import axios from 'axios';

function App() {
  const [activeNav, setActiveNav] = useState('home');
  const [singlePage, setSinglePage] = useState('closed');
  const [clickedMovie, setClickedMovie] = useState({});
  const [keyWord, setKeyWord] = useState('doctor');
  const [page, setPage] = useState('1');
  const apiKey = '89fd381db9ad27b0433e7f45be441eda';
  const searchUrl = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&query=${keyWord}&page=${page}`;


  const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';

  const axiosPopMovies = async()=>{
    const res = await axios.get(popMoviesUrl).then(data=>data.data.results);

    localStorage.setItem('popMoviesWeek', JSON.stringify(res));
  }
  axiosPopMovies()

  const axiosSearch = async ()=>{
    const search = await axios.get(searchUrl).then(data => data.data.results);
    // console.log(search);
    localStorage.setItem('searchResults', JSON.stringify(search))
  };
  axiosSearch();

  const handleSubmit = (key)=>{
    setKeyWord(key)
    axiosSearch(); 
      setActiveNav('search');
      console.log(keyWord)
  }


  return (
    <div>

        <header>
            <h3>KinoMak</h3>
            <div>
                <label htmlFor="search">
                    <input onChange={(evt)=>{handleSubmit(evt.target.value)}} type="text" />
                    <button onClick={()=>{handleSubmit()}} >search</button>
                </label>
            </div>
            <nav>
                <button onClick={()=>{setActiveNav('home')}}>home</button>
                <button onClick={()=>{setActiveNav('library')}}>library</button>
            </nav>

        </header>


      {activeNav === 'home' ?
       <Home getClickedMovie={(clickedMovie)=>{setClickedMovie(clickedMovie)}} 
      openModal={(singlePage)=>{setSinglePage(singlePage)}}/> : activeNav === 'library' ?
       <Library openModal={(singlePage)=>{setSinglePage(singlePage)}} getClickedMovie={(clickedMovie)=>{setClickedMovie(clickedMovie)}} /> : activeNav === 'search' ? 
       <Search keyWord={keyWord} openModal={(singlePage)=>{setSinglePage(singlePage)}} getClickedMovie={(clickedMovie)=>{setClickedMovie(clickedMovie)}} />
      : alert('Rendering error!>')}

      {singlePage === 'open' ? <SinglePage closeSinglePage={(singlePage)=>{setSinglePage(singlePage)}}
       clickedMovieData={clickedMovie}/> : console.log('singlePage closed!')}

        <footer>
            <h2> &copy;reserved</h2>
        </footer>
    </div>
  );
}

export default App;
