import React from 'react'
import { useEffect } from 'react'

function Popular() {

    const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';


  function getPopMovies(){
    fetch(popMoviesUrl)
      .then(res => res.json())
      .then(data => createMovEl(data.results))
  }
  useEffect(()=>{
    getPopMovies()
    })

    let createMovEl = (data)=>{
      data.forEach(movie => {
          const baseUrlImg = 'http://image.tmdb.org/t/p/w500';
          const {title, poster_path, vote_average} = movie;
          const movieEl = document.createElement('div');

          movieEl.innerHTML = `
          <h3>${title}</h3>
          <img src="${baseUrlImg + poster_path}" alt="" />
          <span>${vote_average}</span>
          `
          return movieEl
      });
    }

  return (
    <div className='most_popular'>

        {movieEl}

      </div>
  )
}

export default Popular
