import './App.css';
import Home from './components/home/Home'
import Library from './components/library/Library'
import Header from './components/header-footer/Header';
import {useState} from 'react'

function App() {
  const [activeNavHeader, setActiveNavHeader] = useState('home')

  return (
    <div>
      <Header setActiveNavHeader ={setActiveNavHeader} />
      {activeNavHeader === 'home' ? <Home/> : <Library/>}
    </div>
  );
}

export default App;
