import { useState } from "react";
import React from 'react'

export default function SinglePage(props) {
    const imgBaseUrl = 'http://image.tmdb.org/t/p/w500';
    const movie = props.clickedMovieData;
    const wArr = props.watchedArr;
    const [ wBtn , setWBtn ] = useState('+')
    // console.log(movie.id);

    const handleBtnWatched = ()=>{
      let getLocal = localStorage.getItem('listWatched');
      wArr.includes(movie.id) ? setWBtn('-') : setWBtn('+');

      props.handleWatched()
    }

  return (
    <div className='single_page'>
       <h2>{movie.title}</h2> 
      <div className="single">
       <img src={imgBaseUrl+movie.poster_path} alt={movie.title}/>
       <span>Rate: {movie.vote_average}</span>
       <p>{ movie.overview }</p>      
       <div>
           <h3>Add to:</h3>
       <button onClick = {()=>{handleBtnWatched()}} >Watched</button>
       <button onClick = {()=>{props.handleToWatch()}} >To Watch</button>
       </div>
       <button className="close_btn" onClick={()=>{props.closeSinglePage('closed')}}>X</button>
      </div>
    </div>
  )
}
