import './App.css';
import Home from './components/home/Home'
import Library from './components/library/Library'
import Search from './components/search/Search'
import SinglePage from './components/SinglePage'
import Loading from './components/Loading';
import {useEffect, useState} from 'react'
import axios from 'axios';

function App() {
    const [popMovies, setPopMovies] = useState([])
  const [activeNav, setActiveNav] = useState('home');
  const [singlePage, setSinglePage] = useState('closed');
  const [clickedMovie, setClickedMovie] = useState({});
  const [keyWord, setKeyWord] = useState('doctor');
  const [page, setPage] = useState('1');
  const apiKey = '89fd381db9ad27b0433e7f45be441eda';
  const searchUrl = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&query=${keyWord}&page=${page}`;
  const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';

  let watched = [];
  let idArr = [];

  const handleWatched = ()=>{
    let getLocal = localStorage.getItem('listWatched');
    getLocal === null ? watched = [] : watched = JSON.parse(getLocal);

    const pushData = ()=>{
      watched.push(clickedMovie);



      idArr.push(movie.id);
    }

    (!idArr.includes(clickedMovie.id) === true ) ? pushData() :  console.log('Already in Watched List!');

    localStorage.setItem('listWatched' , JSON.stringify(watched));
    console.log(watched);
    console.log(clickedMovie);
    console.log(clickedMovie.id);
    console.log(idArr)
    console.log(!idArr.includes(clickedMovie.id));
  }
  // localStorage.setItem('listToWatch' , ' ');



  useEffect(()=>{
      axiosPopMovies();
      axiosSearch();
    }, []);
      const axiosPopMovies = async()=>{
          const res = await axios.get(popMoviesUrl).then(data=>data.data.results);

          setPopMovies(res);

        }       
        const axiosSearch = async ()=>{
            const search = await axios.get(searchUrl).then(data => data.data.results);
            localStorage.setItem('searchResults', JSON.stringify(search))
        };



  const handleSubmit = (key)=>{
    setKeyWord(key)
    axiosSearch(); 
      setActiveNav('search');
  }


  return (
    <div>

        <header>
            <h3>KinoMak</h3>
            <div>
                <label htmlFor="search">
                    <input onChange={(evt)=>{handleSubmit(evt.target.value)}} type="text" />
                    <button onClick={()=>{handleSubmit()}} >search</button>
                </label>
            </div>
            <nav>
                <button onClick={()=>{setActiveNav('home')}}>home</button>
                <button onClick={()=>{setActiveNav('library')}}>library</button>
            </nav>

        </header>


      { popMovies !== null ?
      (activeNav === 'home' ? <Home popMovies={popMovies} getClickedMovie={(clickedMovie)=>{setClickedMovie(clickedMovie)}} openModal={(singlePage)=>{setSinglePage(singlePage)}}/> 
      : activeNav === 'library' ? <Library openModal={(singlePage)=>{setSinglePage(singlePage)}} getClickedMovie={(clickedMovie)=>{setClickedMovie(clickedMovie)}} /> 
      : activeNav === 'search' ? <Search keyWord={keyWord} openModal={(singlePage)=>{setSinglePage(singlePage)}} getClickedMovie={(clickedMovie)=>{setClickedMovie(clickedMovie)}} />
      : alert('Rendering error!>'))
      : <Loading/>
      }

      {singlePage === 'open' ? <SinglePage handleWatched={()=>{handleWatched()}} closeSinglePage={(singlePage)=>{setSinglePage(singlePage)}}
       clickedMovieData={clickedMovie}/> : console.log('singlePage closed!')}

        <footer>
            <h2> &copy;reserved</h2>
        </footer>
    </div>
  );
}

export default App;
