import React from 'react'
import {useState, useEffect } from 'react'
import axios from 'axios';

function Popular() {
    const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';
  const [popMovies, setPopMovies] = useState()
  const imgBaseUrl = 'http://image.tmdb.org/t/p/w500'
  useEffect(()=>{
    const axiosPopMovies = async()=>{
      const res = await axios.get(popMoviesUrl);
      console.log(res.data.results);
      res.status === 200 ? setPopMovies(res.data.results) : console.log('axios failed');
    }
    axiosPopMovies()
    }, []);


  return (
    <div id = 'most_popular'>
        <div className="popMovie">
         {popMovies.map(movie =>(
           <li key={movie.id} className='pop_movie_list_item' >
             <h3>{movie.title}</h3>
             <img src={imgBaseUrl + movie.poster_path} alt={movie.title} />
             <span className='avg_rating'> {movie.vote_average} </span>
           </li>
         ))}
        </div>



    </div>
  )
}

export default Popular
