import React from 'react'
import {useEffect } from 'react'
import axios from 'axios';

function Popular() {
    const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';
  // const [popMovies, setPopMovies] = useState([]);
  const imgBaseUrl = 'http://image.tmdb.org/t/p/w500';
  useEffect(()=>{
    const axiosPopMovies = async()=>{
      const res = await axios.get(popMoviesUrl).then(data=>data.data.results);
      // console.log(res);
      localStorage.setItem('popMoviesWeek', JSON.stringify(res));
    }
    axiosPopMovies()
    }, []);
    const popMovies = JSON.parse(localStorage.getItem('popMoviesWeek'));
    // console.log(popMovies);

    const openModal = (id)=>{

      const clickedMovie =  popMovies.find(elem => elem.id === id);

      console.log('modal opened -' + id)
      console.log(clickedMovie)
    }


  return (
    <div id = 'most_popular'>
      <h1>Top 20 Of The Week!</h1>
        <div className="popMovie">

         {popMovies.map(movie =>(

           <div key={movie.id} className='pop_movie_list_item' onClick = {()=>{openModal(movie.id)}}>
             <h3>{movie.title}</h3>
             <img src={imgBaseUrl + movie.poster_path} alt={movie.title} className='most_popular_img'/>
             <br />
             <span className='avg_rating'> Rate: {movie.vote_average} </span>
           </div>
           ))}
        </div>



    </div>
  )
}

export default Popular
