import './home.css'
import React from 'react'
import { useEffect } from 'react'


export default function Home() {
  const popMoviesUrl = 'https://api.themoviedb.org/3/trending/movie/week?api_key=89fd381db9ad27b0433e7f45be441eda';
  let movieTitle = '';

  async function getPopMovies(){
    let title = await fetch(popMoviesUrl)
      .then(res => res.json())
      .then(data => data.results[0].title = movieTitle )
  } ;

  useEffect(()=>{
    getPopMovies()
    })

  // console.log(data.results)

  return (
    <div>
      <div className='most_popular'>
        <h3>{movieTitle}</h3>
      </div>
    </div>
  )
}
